import { useEffect, useState } from "react";
import sanityClient from "../../client.js";

export default function AllPosts() {
  const [allPostsData, setAllPosts] = useState(null);

  useEffect(() => {
    sanityClient
      .fetch(
        `*[_type == "puppy"]{
        title,
        image{
          asset->{
          _id,
          url
        },
        featured,
        sex{
            reference->{
                url
            }
        }
      }
    }`
      )
      .then((data) => setAllPosts(data))
      .catch(console.error);
  }, []);

  return (
    <div>
      <h2>Blog Posts</h2>
      <h3>Welcome to my blog posts page!</h3>
      <div>
        {allPostsData &&
          allPostsData.map((post, index) => (
              <span key={index}>
                <img src={post.image.asset.url} alt="" />
                <span>
                  <h2>{post.title}</h2>
                  <h3>{post.featured}</h3>
                  <p>{post.sex.reference}</p>
                </span>
              </span>
          ))}
      </div>
    </div>
  );
}
