const Info = () => {
    const [allData, setAll] = useState(null)

    useEffect(() => {
      sanityClient
        .fetch(
          `*[_type == "about"] {
              title,
              body{
                en[0]{
                  children[0]{
                    text
                  } 
                }
              },
              title2,
              body2{
                en[0]{
                  children[0]{
                    text
                  } 
                }
              },
              image{
                asset->{
                  _id,
                  url
                }
              }
          }`
        )
        .then((data) => setAll(data))
        .catch(console.error)
    }, [])
  
    console.log(setAll)
    return (
        <div className="infoPage">
            <h1>Procedures, Info and Guarantees</h1>
            <h1>Dogs Environment</h1>
        </div>
    )
}

export default Info
