import { useEffect, useState, Component } from "react";
import sanityClient from "../../client.js";

export default function AllPosts() {
  const [allPostsData, setAllPosts] = useState(null);

  useEffect(() => {
    sanityClient
      .fetch(
        `*[_type == "puppy"] {
            title,
            sex,
            breedSelect,
            breedType,
            litter,
            image{
                asset->{
                _id,
                url
                }
            },
            body{
                en->{
                    text
                }
            }
            
        }`
      )
      .then((data) => setAllPosts(data))
      .catch(console.error);
  }, []);

  console.log(setAllPosts)
  return (
      
    <div>
      <h2>Blog Posts</h2>
      <h3>Welcome to my blog posts page!</h3>
      <div>
        {allPostsData &&
          allPostsData.map((post, index) => (
              <span key={index}>
                <img src={post.image.asset.url} alt="" />
                <span>
                  <h2>{post.title}</h2>
                  <h3>{post.sex}</h3>
                  <h4>{post.breedSelect}</h4>
                  <h5>{post.breedType}</h5>
                  <p>{post.body.en.text}</p>
                  <p>Litter {post.litter._ref}</p>
                </span>
              </span>
          ))}
      </div>
    </div>
  );
}
