import { useEffect, useState, Component } from "react"
import sanityClient from "../../client.js"

export default function FeaturedPups() {
  const [allData, setAll] = useState(null)

  useEffect(() => {
    sanityClient
      .fetch(
        `*[_type == "puppy" && featured == true] {
            title,
            sex,
            breedSelect,
            breedType,
            "litter": litter->title,
            body{
              en[0]{
                children[0]{
                  text
                } 
              }
			      },
            image{
              asset->{
                _id,
                url
              }
            }
        }`
      )
      .then((data) => setAll(data))
      .catch(console.error)
  }, [])

  console.log(setAll)
  return (
    <div className="featuredPups">
      <h2>Featured Pups</h2>
      <div className="featuredPupCards">
        {allData &&
          allData.map((data, index) => (
            <div className="pupCard" key={index}>
              <img src={data.image.asset._ref} alt="" />
              <div>
                <h>{data.title}</h>
                <h3>{data.sex}</h3>
                <h4>{data.breedSelect}</h4>
                <h5>{data.breedType}</h5>
                <p>{data.body.en.children.text}</p>
                <p>{data.litter}</p>
              </div>
            </div>
          ))}
      </div>
    </div>
  )
}
