import { useEffect, useState, Component } from "react"
import sanityClient from "../../client.js"

export default function Pups() {
  const [allData, setAll] = useState(null)

  useEffect(() => {
    sanityClient
      .fetch(
        `*[_type == "puppy"] {
            title,
            sex,
            breedSelect,
            breedTypev
            available,
            "litter": litter->title,
            body{
              en[0]{
                children[0]{
                  text
                } 
              }
			      },
            image{
              asset->{
                _id,
                url
              }
            }
        }`
      )
      .then((data) => setAll(data))
      .catch(console.error)
  }, [])

  console.log(setAll)
  return (
    <div className="pups">
      <div className="pupsWrap">
        <h2>Featured Pups</h2>
        <div className="pupCards">
          {allData &&
            allData.map((data, index) => (
              <div key={index} className="pupCard">
              <img src={data.image.asset.url} alt="" />
                <div>
                  <h2>{data.title}</h2>
                  <h3>{data.sex}</h3>
                  <h4>{data.breedSelect}</h4>
                  <h5>{data.breedType}</h5>
                  <p>{data.litter}</p>
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  )
}
