import Component from 'react'
import Routes from "../pages/routes"

 class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      prevScrollpos: window.pageYOffset,
      visible: true
    };
  }

  render() {
    return (
      <header 
        className={!this.state.visible ? "navbar" : "navbar--hidden"}
      >
      <Routes/>
      </header>
    );
  }
}
